	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double lon, are, rad;
		 Scanner sc = new Scanner(System.in);
		System.out.println("Inserte el radio de la circunferencia:");
		rad = sc.nextDouble();
		lon = 2* 3.14 * rad;
		are = 3.14* rad * rad;
		System.out.println(String.format("Un círculo de radio %f tendrá una longitud de %f y un área de %f", rad, lon, are));
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

