package serangetti;

/**
 * @author Java
 *
 */
public class Ej2 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
	
		int x;
		int y;
		double n;
		double m;
		//divido esto para hacerlo más prolijo viste
		x = 2;
		y = 8;
		n = 3.3;
		m = 5.2;
		
		System.out.println("Hola, buenos dias.");
		System.out.println();
		System.out.println();
		System.out.println("X + Y=" +(x+y));
		System.out.println("X - Y=" +(x-y));
		System.out.println("X * Y=" +(x*y));
		System.out.println("X / Y=" +(x/y));
		System.out.println("X % Y=" +(x%y));
		System.out.println("N + M =" + (n+m));
		System.out.println("N - M =" + (n-m));
		System.out.println("N * M =" + (n*m));
		System.out.println("N / M =" + (n/m));
		System.out.println("N % M =" + (n%m));
		System.out.println(String.format("El doble de X es %d, el de Y es %d, el de N es %f y el de M es %f", x*2, y*2, n*2, m*2));
		System.out.println("La suma de todas las variables es: " + (x+y+n+m));
		System.out.println("El producto de todas las variables es: " + (x*y*n*m));
		System.out.println("Y listo.");
	

	}

}
